// Import necessary modules and components
import React, { Component } from 'react';
import BasicInfo from './basicInfo'; // Importing the BasicInfo component
import './App.css'; // Importing the App's styling

// Define the main App component
class App extends Component {
  // Constructor is a special function that initializes the component with properties and state
  constructor(props) {
    // 'super(props)' calls the constructor of the parent class (Component in this case)
    super(props);

    // The state object stores the dynamic data for the component
    this.state = {
      // 'people' is an array that holds information about different individuals
      people: [
        // Each person is represented as an object with properties like 'name', 'title', 'phoneNumber'
        {
          name: 'Chastity Matthews',
          title: 'Software Engineer',
          phoneNumber: '(843) 345-0709',
          // Add other properties as needed
        },
        {
          name: 'Beyonce Knowles',
          title: 'Software Engineer',
          phoneNumber: '(913) 854-0624',
          // Add other properties as needed
        },
        {
          name: 'Barrack Obama',
          title: 'Software Engineer',
          phoneNumber: '(294) 7301-6729',
          // Add other properties as needed
        },
        {
          name: 'Tyra Banks',
          title: 'Software Engineer',
          phoneNumber: '(982) 529-5492',
          // Add other properties as needed
        },
        {
          name: 'Rachael Ray',
          title: 'Chef',
          phoneNumber: '(704) 837-8492',
          // Add other properties as needed
        },
      ],
    };
  }

  // The render method returns the JSX (HTML-like syntax) that defines the component's UI
  render() {
    // The return statement wraps the JSX in a 'div' element
    return (
      <div>
        {/* Use the map function to iterate over each person in the 'people' array */}
        {this.state.people.map((person, index) => (
          // Render the BasicInfo component for each person, passing the 'key' and 'person' as props
          <BasicInfo key={index} person={person} />
        ))}
      </div>
    );
  }
}

// Export the App component as the default export for this file
export default App;
